Scripts.OnHide:
			for entry, name in next, UISpecialFrames do if (name == this:GetName()) then tremove(UISpecialFrames, entry) end end
			this.button:SetButtonState("NORMAL",0);

			PlaySound("igCharacterInfoClose");
            FeedbackUI_Hide();
            FeedbackUI_SetupWelcome();

Scripts.OnTextChanged:
if ( ( this:GetText() ~= this.default ) and ( this:GetText() ~= this.blankString ) ) then
	this.parentform.submit:Enable();
else
	this.parentform.submit:Disable();
end
ScrollingEdit_OnTextChanged();

Scripts.OnLoad:
this.scroll = this:GetParent():GetParent();
if ( not this.scroll.buttons ) then
	this.scroll.buttons = {}
end
table.insert(this.scroll.buttons, this);
this.index = table.maxn(this.scroll.buttons);
this.name = this:GetName();
this.text = getglobal(this.name.."Text");
this.rightText = getglobal(this.name.."RightText");
this.func = nil;
this.text:SetText(this.name);
this.highlightColor = { ["r"] = 1, ["g"] = 1, ["b"] = 1, ["a"] = 1 };
this.normalColor = { ["r"] = 1, ["g"] = .82, ["b"] = 0, ["a"] = 1 };

this:Raise();
this:Disable();
this.scroll = this:GetParent():GetParent();
this.panel = this.scroll:GetParent();
this.scroll.upbtn = this;

this:Raise();
this:Disable();
this.scroll = this:GetParent():GetParent();
this.panel = this.scroll:GetParent();
this.scroll.downbtn = this;

this:Raise();
this:Disable();
this.scroll=this:GetParent():GetParent():GetParent();
this.scroll.thumb = this;
this.panel = this.scroll:GetParent();

this.scroll = this:GetParent();
this.name = this:GetName();
this.text = getglobal(this.name.."Text");
this.rightText = getglobal(this.name.."RightText");
this.func = nil;
this.text:SetText(this.name);
this.highlightColor = { ["r"] = 1, ["g"] = 1, ["b"] = 1, ["a"] = 1 };
this.normalColor = { ["r"] = 1, ["g"] = .82, ["b"] = 0, ["a"] = 1 };

Scripts.OnEnter:
			this.btn:LockHighlight();
			this.text:SetTextColor(this.highlightColor.r, this.highlightColor.g, this.highlightColor.b, this.highlightColor.a);
			this.rightText:SetTextColor(this.highlightColor.r, this.highlightColor.g, this.highlightColor.b, this.highlightColor.a);
            if ( this.tooltip ) then
                this.scroll:GetParent().ShowTooltip(this.scroll:GetParent(), this)
            end

Scripts.OnLeave:
			this.btn:UnlockHighlight();
			this.text:SetTextColor(this.normalColor.r, this.normalColor.g, this.normalColor.b, this.normalColor.a);
			this.rightText:SetTextColor(this.normalColor.r, this.normalColor.g, this.normalColor.b, this.normalColor.a);
            if ( this.tooltip ) then
                this.scroll:GetParent().HideTooltip(this.scroll:GetParent())
            end

Scripts.OnEnter:
local parent = this.parent
parent.text:SetTextColor(parent.highlightColor.r, parent.highlightColor.g, parent.highlightColor.b, parent.highlightColor.a);
parent.rightText:SetTextColor(parent.highlightColor.r, parent.highlightColor.g, parent.highlightColor.b, parent.highlightColor.a);

Scripts.OnLeave:
local parent = this.parent
parent.text:SetTextColor(parent.normalColor.r, parent.normalColor.g, parent.normalColor.b, parent.normalColor.a);
parent.rightText:SetTextColor(parent.normalColor.r, parent.normalColor.g, parent.normalColor.b, parent.normalColor.a);

Scripts.OnClick:
PanelTemplates_Tab_OnClick(FeedbackUI);
PlaySound("igCharacterInfoTab");
for _, frame in next, feedbackFrames do
	frame:Hide();
end
this.frame:Show();
if ( this.frame.panels ) then
	for _, panel in pairs(this.frame.panels) do
		pcall(panel.Show, panel);
	end
end

Scripts.OnLoad:
this:RegisterForClicks("LeftButtonUp");
this:SetHeight("42");
this:SetWidth("34");
this.alert = this:GetParent();
if ( not this.alert.buttons ) then
    this.alert.buttons = {}
end
table.insert(this.alert.buttons, this);
this.index = table.maxn(this.alert.buttons);
this:ClearAllPoints()
this:SetPoint("BOTTOM", "UIParent", "BOTTOM", 0, 130)
this.tooltip = "";
this.lines = {}

Scripts.OnEnter:
			GameTooltip:SetOwner(this, "ANCHOR_LEFT");
            for _, line in ipairs(this.lines) do
                GameTooltip:AddDoubleLine(line.left, line.right);
            end
            GameTooltipTextLeft2:SetFontObject("GameTooltipHeaderText");
            GameTooltipTextLeft2:SetText(FEEDBACKUI_BLUE .. ( GameTooltipTextLeft2:GetText() or "" ) )

            if ( SHOW_NEWBIE_TIPS == "1" ) then
                GameTooltip:AddLine(FEEDBACKUI_SURVEYNEWBIETEXT, NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b, 1);
            end
            GameTooltip:Show();

Scripts.OnClick:

UIDropDownMenu_SetSelectedValue(this.alert.panel.ddlCategory, "All")
UIDropDownMenu_SetSelectedValue(this.alert.panel.ddlStatus, "Available")
this.alert.panel.LoadStatus(this.alert.panel.ddlStatus);
this.alert.panel.LoadCategory(this.alert.panel.ddlCategory);
this.alert.panel.Expand(this.alert.panel, g_FeedbackUI_surveysTable["Alerts"][this.index].type)
this.alert.panel.table = {};
this.alert.panel.PopulateTable(this.alert.panel)
this.alert.panel.ScrollToSurvey(this.alert.panel, g_FeedbackUI_surveysTable["Alerts"][this.index])
if ( this:IsVisible() ) then
    GameTooltip:SetOwner(this, "ANCHOR_LEFT");
    for _, line in ipairs(this.lines) do
        GameTooltip:AddDoubleLine(line.left, line.right);
    end
    GameTooltipTextLeft2:SetFontObject("GameTooltipHeaderText");
    GameTooltipTextLeft2:SetText(FEEDBACKUI_BLUE .. GameTooltipTextLeft2:GetText())

    if ( SHOW_NEWBIE_TIPS == "1" ) then
        GameTooltip:AddLine(FEEDBACKUI_SURVEYNEWBIETEXT, NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b, 1);
    end
    GameTooltip:Show();
end

if ( this.sortType ) then
	this.panel.SortResults(this.panel, this.sortType);
end
PlaySound("igMainMenuOptionCheckBoxOn");
